fromPath.code:
public function fromPath(string $path, string $sep = '|', $parent = null): ?string
{
  if ($this->check()) {
    if (!empty($sep)) {
      $parts = explode($sep, $path);
    }
    else{
      $parts = [$path];
    }

    if (null === $parent) {
      $parent = $this->default;
    }

    foreach ($parts as $p){
      if (!($parent = $this->fromCode($p, $parent))) {
        break;
      }
    }

    return $parent ?: null;
  }

  return null;
}

