textValueOptionsRef.code:
public function textValueOptionsRef(string|array $id, string $text = 'text', string $value = 'value', ...$additionalFields): ?array
{
  $res = [];

  if (is_string($id) && !Str::isUid($id)) {
    $id = [$id];
  }

  // Get the full options for the provided codes.
  if (!is_array($id)) {
    $id = [$id];
  }

  if ($id = $this->fromCode(...$id)) {
    $opts = $this->fullOptionsRef($id);
    $cfg = $this->getCfg($id) ?: [];
    $i   = 0;
    foreach ($opts as $k => $o) {
      if (!isset($is_array)) {
        $is_array = \is_array($o);
      }

      $res[$i] = [
        $text => $is_array ? $o[$this->fields['text']] : $o,
        $value => $is_array ? $o[$this->fields['id']] : $k
      ];
      if (!empty($cfg['show_code'])) {
        $res[$i][$this->fields['code']] = $o[$this->fields['code']];
      }

      $i++;
    }
  }

  return $res;
}

