public function applyAllTemplates(): ?int
{
  $tot = 0;
  $tids = $this->items($this->getMagicTemplateId());
  foreach ($tids as $tid) {
    foreach ($this->getAliasItems($tid) as $id) {
      $tot += $this->applyTemplate($id);
    }
  }

  $tids = $this->getAliasItems($this->getTemplatesTemplateId());
  foreach ($tids as $tid) {
    foreach ($this->getAliasItems($tid) as $id) {
      $tot += $this->applyTemplate($id);
    }
  }

  return $tot;
}

