public function updateAllTemplates(): ?int
{
  if ($id = $this->fromCode('list', 'templates')) {
    $res = 0;
    foreach ($this->itemsRef($id) ?? [] as $a) {
      $res += (int)$this->updateTemplate($a);
    }

    return $res;
  }

  return null;
