applySubTemplates.code:
public function applySubTemplates($id, $target = null): int 
{
  $num = 0;
  if (!$target) {
    $target = $id;
  }

  if (($cfg = $this->getCfg($id)) && ($id_tpl = $cfg['id_template'])) {
    if (($id_tpl === $this->getSubpluginTemplateId()) && (count($this->parents($id)) <= 3)) {
      $id_tpl = $this->getPluginTemplateId();
    }
    $items = $this->nativeOptions($target);
    if (!empty($items)) {
      foreach ($items as $it) {
        $scfg = $this->getCfg($it['id']);
        if (empty($scfg['container'])) {
          if ($it['id_alias'] !== $id_tpl) {
            if ($this->setAlias($it['id'], $id_tpl)) {
              $this->deleteCache($it['id']);
              $num++;
            }
            else {
              throw new Exception(X::_("Impossible to set the alias for the option")." ".$it['id']);
            }
          }

          $this->applyTemplate($it['id']);
        }
        else {
          $this->applySubTemplates($id, $it['id']);
        }
      }
    }
  }

  return $num;
}

applyTemplate.code:
public function applyTemplate(string $id, bool $rootAlso = false): ?int
{
  if (!$this->getMagicTemplateId()) {
    return null;
  }

  if (!($idAlias = $this->alias($id))) {
    throw new Exception(X::_("Impossible to apply a template, the option must be aliased"));
  }

  if (($idAlias === $this->getSubpluginTemplateId()) && (count($this->parents($id)) === 3)) {
    $idAlias = $this->getPluginTemplateId();
  }

  $tot = 0;
  $templateParent = $this->parent($idAlias);
  if ($templateParent['id'] !== $this->getMagicTemplateId()) {
    if (!$templateParent['id_alias']) {
      throw new Exception(X::_("Impossible to apply a template, the template's parent must have an alias"));
    }

    if ($templateParent['id_alias'] !== $this->getTemplatesTemplateId()) {
      throw new Exception(X::_("Impossible to apply a template, the template's parent must be aliased with the templates' list"));
    }
  }

  /*
  if ($rootAlso) {
    $opt = $this->option($id);
    $topt = $this->option($idAlias);
    unset($opt['id_alias'], $opt['alias']);
    if ((json_encode($opt) !== json_encode($topt)) && $this->set($id, $topt)) {
      $tot++;
    }
  }
  */

  $this->unsetCfg($id);

  foreach ($this->items($idAlias) as $tid) {
    $tot += $this->applyChildTemplate($tid, $id);
  }

  return $tot;
}

applyChildTemplate.code:
public function applyChildTemplate($idSubtemplate, $target): int
{
  $tot = 0;
  if ($idSubtemplate === $target) {
    throw new Exception("The template cannot apply to itself");
  }

  $subtpl = $this->rawOption($idSubtemplate);
  $f =& $this->class_cfg['arch']['options'];
  $cfg = $subtpl[$f['cfg']] ? json_decode($subtpl[$f['cfg']], true) : [];
  $foptions = $this->rawOptions($target);
  $update = true;
  if (!($o = X::getRow($foptions, ['id_alias' => $idSubtemplate]))) {
    if ($o = X::getRow($foptions, ['code' => $subtpl[$f['code']]])) {
      if ($o['id'] === $idSubtemplate) {
        X::log([
          $idSubtemplate,
          $target,
          $this->option($target),
          $subtpl,
          $foptions,
          $this->items($target),
          $this->db->rselectAll('bbn_options', [], [$f['id_parent'] => $target])
        ], 'optionsFail');
      }

      if ($this->setAlias($o['id'], $idSubtemplate)) {
        $tot++;
      }
    }
    elseif ($id = $this->add([
      $f['id_parent'] => $target,
      $f['code']      => $subtpl[$f['code']],
      $f['id_alias']  => $idSubtemplate
    ])) {
      $o = $this->rawOption($id);
      $tot++;
      $update = false;
    }
    else {
      throw new Exception(X::_("Impossible to add the option"));
    }
  }

  if ($update && !empty($o)) {
    $upd = [];
    if ($o[$f['code']] !== $subtpl[$f['code']]) {
      $upd[$f['code']] = $subtpl[$f['code']];
    }
    if (!empty($o[$f['text']])) {
      $upd[$f['text']] = null;
    }
    if (!empty($o[$f['value']])) {
      $upd[$f['value']] = null;
    }
    if (!empty($o[$f['cfg']])) {
      $upd[$f['cfg']] = null;
    }

    if (!empty($upd)) {
      if ($this->db->update($this->class_cfg['table'], $upd, [$f['id'] => $o['id']])) {
        $tot++;
      }
    }
  }

  $this->deleteCache(null, true, true);

  X::log([$idSubtemplate, $subtpl, $cfg, $this->items($idSubtemplate)], 'import_options');
  foreach ($this->items($idSubtemplate) as $tid) {
    $tot += $this->applyChildTemplate($tid, $o['id']);
  }
  if (!empty($cfg['id_template'])) {
    $this->applySubTemplates($o['id']);
  }

  $this->deleteCache();
  return $tot;
}

getParentTemplateId.code:
public function getParentTemplateId(string $id): ?string
{
  $templateId1 = $this->getTemplatesTemplateId();
  $templateId2 = $this->getMagicTemplateId();
  while ($idParent = $this->getIdParent($id)) {
    // root
    if ($idParent === $id) {
      return null;
    }

    if (in_array($idParent, [$templateId1, $templateId2], true) || in_array($this->getIdAlias($idParent), [$templateId1, $templateId2], true)) {
      return $id;
    }

    $id = $idParent;
  }

  return null;
}

usedTemplate.code:
public function usedTemplate(string $id): ?string
{
  if ($this->exists($id) && ($idAlias = $this->getIdAlias($id))) {
    $templateId1 = $this->getMagicTemplateId();
    $templateId2 = $this->getTemplatesTemplateId();
    $id = $idAlias;
    while ($idParent = $this->getIdParent($id)) {
      // root
      if ($idParent === $id) {
        return null;
      }

      if (($idParent === $templateId1) || ($this->getIdAlias($idParent) === $templateId2)) {
        return $id;
      }

      $id = $idParent;
    }
  }

  return null;
}

