private function _fullTree(string $id): ?array
{
  if (Str::isUid($id)) {
    if ($opts = $this->fullOptions($id)) {
      foreach ($opts as &$o){
        $o['items'] = $this->_fullTree($o['id']);
      }
      unset($o);
    }

    return $opts;
  }

  return null;
}

