public function merge(string $id, array $data, array|null $cfg = null)
{
  if ($this->check()
      && ($o = $this->option($id))
  ) {
    $c =& $this->fields;
    $o[$c['text']] = $this->rawText($o[$c['id']]);
    if (!empty($data)) {
      $data = array_merge($o, $data);
      $this->_prepare($data);
      if (isset($data[$c['id']])) {
        unset($data[$c['id']]);
      }
    }

    if ($cfg) {
      $data[$c['cfg']] = array_merge($this->getRawCfg($id) ?: [], $cfg);
    }

    // id_parent cannot be edited this way
    if ($res = $this->db->update(
      $this->class_cfg['table'],
      $data,
      [$c['id'] => $id]
    )
    ) {
      $this->deleteCache($id);
      return $res;
    }

    return 0;
  }

  return null;
}

