setProp.code:
public function setProp($id, $prop)
{
  if (!empty($id) && !empty($prop) && ($o = $this->optionNoAlias($id))) {
    $args = \func_get_args();
    if (\is_string($prop) && isset($args[2])) {
      $prop = [$prop => $args[2]];
    }

    if (\is_array($prop)) {
      X::log([$o, $prop], "set_prop");
      $change = false;
      foreach ($prop as $k => $v){
        if (!array_key_exists($k, $o) || ($o[$k] !== $v)) {
          $change = true;
          $o[$k]  = $v;
        }

      }

      if ($change) {
        return $this->set($id, $o);
      }
    }

    return 0;
  }

  return null;
}

