run.code:
function($start, $end = NULL)
  {
    if ($this->db_cfg && !empty($this->db_cfg['values'])) {
      if (is_string($start)) {
        $start = strtotime($start.(strlen($start) === 10 ? ' 00:00:00' : ''));
      }

      if (!$start || !is_int($start)) {
        throw new Error(dgettext(X::tDom(), 'Impossible to read the given start date'));
      }

      if (!$this->is_total) {
        if (!$end || ($end <= $start)) {
          $end = mktime(23, 59, 59, Date('n', $start), Date('j', $start), Date('Y', $start));
        }

        if (!$end || !is_int($end)) {
          throw new Error(dgettext(X::tDom(), 'Impossible to read the given end date'));
        }
      }

      $vals = [];
      foreach ($this->db_cfg['values'] as $v) {
        if (!$this->is_total && ($v === self::$_placeholder.'2')) {
          $vals[] = $end;
        }
        else {
          $vals[] = $v === self::$_placeholder ? $start : $v;
        }
      }

      $cfg           = $this->db_cfg;
      $cfg['values'] = $vals;
      return $this->db->selectOne($cfg);
    }
  }

update.code:
function($variant = NULL, $start = NULL)
  {
    if ($this->check()) {
      if (!$variant) {
        $variant = 'default';
      }

      if (!($real_start = $this->db->selectOne('bbn_statistics', 'MAX(day)', ['id_option' => $this->id_option, 'code' => $variant]))) {
        if ($start) {
          $real_start = $start;
        }
        elseif (!empty($this->ocfg['start'])) {
          $real_start = $this->ocfg['start'];
        }
        else {
          $real_start = self::ODATE;
        }
      }

      if (\bbn\Str::isDateSql($real_start)) {
        $num_days  = 0;
        $num       = $this->db->count(
          'bbn_statistics',
          [
            'id_option' => $this->id_option,
            'code' => $variant
          ]
        );
        $today     = date('Ymd');
        $last_res  = null;
        $last_date = $real_start;
        $time      = mktime(
          12,
          0,
          0,
          (int)substr($real_start, 5, 2),
          (int)substr($real_start, 8, 2),
          (int)substr($real_start, 0, 4)
        );
        $test      = date('Ymd', $time);
        while ($test <= $today) {
          $res = $this->run($real_start);
          if ($num_days) {
            X::hdump($res, $this->db->getLastValues());
          }
          else {
            X::hdump($res, $this->db->last(), $this->db->getLastValues());
          }

          $num_days++;
          if (!$res) {
            $res = 0;
          }

          if (($res !== $last_res) || !$num) {
            if ($this->db->count(
              'bbn_statistics', [
              'id_option' => $this->ocfg['id'],
              'code' => $variant,
              'day' => $real_start
              ]
            )
            ) {
              $this->db->update(
                'bbn_statistics', [
                'res' => $res
                ], [
                'id_option' => $this->ocfg['id'],
                'code' => $variant,
                'day' => $real_start
                ]
              );
            }
            else {
              $this->db->insert(
                'bbn_statistics', [
                'id_option' => $this->ocfg['id'],
                'code' => $variant,
                'day' => $real_start,
                'res' => $res
                ]
              );
            }

            $last_res = $res;
            $num++;
          }
          else {
            $this->db->update(
              'bbn_statistics', [
              'day' => $real_start
              ], [
              'id_option' => $this->ocfg['id'],
              'code' => $variant,
              'day' => $last_date
              ]
            );
          }

          $last_date  = $real_start;
          $time      += 24 * 3600;
          $real_start = date('Y-m-d', $time);
          $test       = date('Ymd', $time);
        }

        return $num_days;
      }
    }

    return null;
  }

serie.code:
function($values = 30, $start = NULL, $end = NULL)
  {
    if ($this->check()) {
      if (!$end) {
        $end = date('Y-m-d');
      }

      if (!$start) {
        $start = date('Y-m-d', strtotime($end.' 12:00:00') - ($values * 24 * 3600));
      }

      if (bbn\Str::isDateSql($start, $end)) {
        $res = [
          'labels' => [],
          'series' => []
        ];
        // All the values between start and end
        $all = $this->db->rselectAll(
          'bbn_statistics', ['day', 'res'], [
          [
            'field' => 'id_option',
            'value' => $this->id_option
          ], [
            'field' => 'day',
            'operator' => '>=',
            'value' => $start
          ], [
            'field' => 'day',
            'operator' => '<=',
            'value' => $end
          ]
          ], [
          'day' => 'ASC'
          ]
        );
        // As the row appears with its last date for value we pick the nextr row if the last row is not the last day
        $last = count($all) - 1;
        if (($all[$last]['day'] !== $end) && ($tmp = $this->db->rselect(
          'bbn_statistics', ['day', 'res'], [
          [
            'field' => 'id_option',
            'value' => $this->id_option
          ], [
            'field' => 'day',
            'operator' => '>',
            'value' => $end
          ]
          ], [
          'day' => 'ASC'
          ]
        ))
        ) {
          $all[] = $tmp;
          $last++;
        }

        if (count($all)) {
          $dcurrent = new \DateTime($start);
          $dend     = new \DateTime($end);
          $num_days = (int)$dend->diff($dcurrent)->format('%a');
          $diff     = $num_days;
          $interval = (int)floor(($num_days + 1) / $values);
          $num      = 0;
          $idx      = 0;
          $didx     = 0;
          $pstart   = false;
          while ($diff >= 0) {
            $current = $dcurrent->format('Y-m-d');
            if (!$pstart) {
              $pstart = $current;
            }

            if ($num === $interval) {
              $num = 0;
            }

            if (!$num) {
              if (!empty($this->ocfg['total']) || ($interval === 1)) {
                $res['labels'][$didx] = $current;
              }
              else {
                $res['labels'][$didx] = bbn\Date::format($pstart, 's').' - '.bbn\Date::format($current, 's');
              }

              if (isset($res['series'][$didx]) && empty($this->ocfg['total'])) {
                $res['series'][$didx] = $res['series'][$didx] + $all[$idx]['res'];
              }
              else {
                $res['series'][$didx] = $all[$idx]['res'];
              }

              $pstart = false;
              $didx++;
            }
            elseif (empty($this->ocfg['total'])) {
              $res['labels'][$didx]  = $current;
              $res['series'][$didx] += (int)$all[$idx]['res'];
            }

            if (!$diff) {
              break;
            }

            if ($current === $all[$idx]['day']) {
              $idx++;
            }

            $dcurrent = $dcurrent->add(date_interval_create_from_date_string('1 days'));
            $diff     = (int)$dend->diff($dcurrent)->format('%a');
            $num++;
          }
        }

        return $res;
      }
    }

    return null;
  }

