          ON group_role.id_task = bbn_tasks.id
        JOIN `".$usr_table."`
          ON bbn_tasks_roles.id_user = `".$usr_table."`.`".$usr_fields['id']."`";
          }
        }
      }
      else if ( isset($fields['refs'][$w[0]]) ){
        if ( \is_int($w[2]) ){
          $having .= " AND ".$fields['refs'][$w[0]]." $w[1] ? ";
          array_push($args1, $w[2]);
        }
      }
    }
