sanitize.code:
function(string $url, string $prefix = '')
  {
    $url    = trim($url, '/ ');
    $prefix = trim($prefix, '/ ');
    while (strpos($url, '//')) {
      $url = str_replace('//', '/', $url);
    }

    return $url . ($prefix ? '/' . $prefix : '');
  }

