ttl.code:
function($ttl)
  {
    if (Str::isInteger($ttl)) {
      return (int)$ttl;
    }

    if (\is_string($ttl)) {
      switch ($ttl) {
        case 'xxs':
          return 30;
        case 'xs':
          return 60;
        case 's':
          return 300;
        case 'm':
          return 3600;
        case 'l':
          return 3600 * 24;
        case 'xl':
          return 3600 * 24 * 7;
        case 'xxl':
          return 3600 * 24 * 30;
      }
    }

    return 0;
  }

