  protected function getJs(array $codes, $encapsulated = true): string
  {
    $code = '';
    if (!empty($codes['js'])) {
      $num      = count($codes['js']);
      $root_url = $this->furl;
      foreach ($codes['js'] as $c) {
        $tmp = $c['code'];
        if (empty($this->cfg['nocompil'])) {
          $tmp = <<<JS
window.bbnAddGlobalScript(function() {
  // $num
  window.bbnLoadFile("$c[dir]/$c[file]");
  let bbn_language = "{$this->cfg['lang']}";
  let bbn_root_dir = "$c[dir]/";
  let bbn_root_url = "$root_url";
      $tmp
});
JS;
        }

        if (!empty($tmp)) {
          $code .= $tmp . ($this->cfg['test'] ? str_repeat(PHP_EOL, 5) : PHP_EOL);
        }
      }

      if (!empty($this->cfg['content']['css'])) {
        $code    .= <<<JS
    return (new Promise(function(bbn_resolve, bbn_reject){
      bbn_resolve()
    }))

JS;
        $done_css = [];
        foreach ($this->cfg['content']['includes'] as $lib) {
          if (!empty($lib['css'])) {
            $code .= $this->cp->cssLinks(
                array_map(
                    function ($a) use ($lib, &$done_css) {
                      $done_css[] = $lib['path'] . $a;
                      return $lib['path'] . $a;
                    },
                    $lib['css']
                ),
                $this->cfg['test'],
                $lib['prepend']
            );
          }
        }

        if (!empty($this->cfg['content']['css'])) {
          $undone_css = [];
          foreach ($this->cfg['content']['css'] as $css) {
            if (!in_array($css, $done_css)) {
              $undone_css[] = $css;
            }
          }

          if (!empty($undone_css)) {
            $code .= $this->cp->cssLinks($undone_css, $this->cfg['test']);
          }
        }
      }

      if ($encapsulated) {
        $code = $this->jsMask($code);
      }
    }

    return $code;
  }

