protected function getCss(array $codes)
{
  $code = '';
  if (!empty($codes['css'])) {
    foreach ($codes['css'] as $c) {
      $code .= $c['code'] . ($this->cfg['test'] ? str_repeat(PHP_EOL, 5) : PHP_EOL);
    }
  }

  return $code;
}

