  protected function getComponents()
  {
    $code  = '';
    $codes = [];
    $c     =& $this->cfg;
    if (\is_array($c['content'])) {
      $i        = 0;
      $includes = '';
      foreach ($c['content'] as $name => $cp) {
        foreach ($cp['js'] as $js) {
          $ext = Str::fileExt($js, true);
          //X::dump($codes);
          // A js file with the component name is mandatory
          if ($ext[0] === $name) {
            // Once found only this js file will be used as it should just define the component
            $jsc       = $this->cp->compile([$js], $c['test']);
            $codes[$i] = [
              'name' => $name,
              'js' => $jsc['js'][0]['code']
            ];
            if (!empty($cp['css'])) {
              $cssc = $this->cp->compile($cp['css'], $c['test']);
              if (is_array($cssc) && !empty($cssc['css'])) {
                foreach ($cssc['css'] as $css) {
                  if (!isset($css['code'])) {
                    throw new \Exception("Impossible to get the SCSS code from component " . $cp);
                    //die(var_dump($css));
                  }

                  if ($this->cp->hasLinks($css['code'])) {
                    $includes .= $this->cp->cssLinks($cp['css'], $c['test']);
                    unset($cp['css']);
                    break;
                  }
                }

                $codes[$i]['css'] = array_map(
                    function ($a) {
                      return $a['code'];
                    },
                    $cssc['css']
                );
              }
            }

            if (
                !empty($c['lang'])
                && !empty($cp['lang'])
                && \in_array(X::dirname($js) . "/$name.$c[lang].lang", $cp['lang'], true)
            ) {
              $lang = file_get_contents($this->fpath . X::dirname($js) . "/$name.$c[lang].lang");
              if ($lang) {
                //$lang = json_decode($lang, true);
                $codes[$i]['js'] = "if ( window.bbn ){ bbn.fn.autoExtend('lng', $lang); }" . PHP_EOL . $codes[$i]['js'];
              }
            }

            // Dependencies links
            $dep_path = $this->fpath . $jsc['js'][0]['dir'] . '/';
            if (is_file($dep_path . 'bbn.json')) {
              $json = json_decode(file_get_contents($dep_path . 'bbn.json'), true);
            }
            else {
              if (is_file($dep_path . 'bower.json')) {
                $json = json_decode(file_get_contents($dep_path . 'bower.json'), true);
              }
            }

            if (!empty($json)) {
              if (!empty($json['dependencies'])) {
                $lib = new Cdn\Library($this->db, $this->cfg['lang'], true);
                foreach ($json['dependencies'] as $l => $version) {
                  $lib->add($l);
                }

                if ($cfg = $lib->getConfig()) {
                  if (!empty($cfg['css'])) {
                    $includes .= $this->cp->cssLinks($cfg['css'], $this->cfg['test']);
                  }

                  if (!empty($cfg['js'])) {
                    $includes .= $this->cp->jsLinks($cfg['js'], $this->cfg['test']);
                  }
                }
              }

              if (!empty($json['components'])) {
                /** @todo Add dependent components */
              }
            }

            // HTML inclusion
            $html = [];
            if (!empty($cp['html'])) {
              foreach ($cp['html'] as $f) {
                if ($tmp = $this->cp->getContent($f, $c['test'])) {
                  $component_name = Str::fileExt($f, true)[0];
                  if ($name !== $component_name) {
                    $component_name = $name . '-' . $component_name;
                  }

                  $html[] = [
                    'name' => $component_name,
                    'content' => $tmp
                  ];
                }
              }
            }

            if (!empty($html)) {
              $codes[$i]['html'] = $html;
            }

            $i++;
            break;
          }
        }
      }

      if ($codes) {
        $str = '';
        foreach ($codes as $cd) {
          $str .= "{name: '$cd[name]', script: function(){try{ $cd[js] } catch(e){throw Error('Impossible to load component $cd[name] - ' + e.message);}}";
          if (!empty($cd['css'])) {
            $str .= ', css: ' . json_encode($cd['css']);
          }

          if (!empty($cd['html'])) {
            $str .= ', html: ' . json_encode($cd['html']);
          }

          $str .= '},';
        }

        $code = <<<JAVASCRIPT

(function(){
  return (new Promise(function(bbn_resolve, bbn_reject){
    setTimeout(function(){
      bbn_resolve();
    })
  }))
  $includes
  .then(function(){
    return bbnAddGlobalScript(function(){
      return [$str]
    })
  })
})()
JAVASCRIPT;
      }

      return $code;
    }
  }

