public function isTimeout($id_cron): bool
{
  if ($this->check()
      && ($cron = $this->getCron($id_cron))
      && ($path = $this->getLogPath($cron))
      && is_file($path)
  ) {
    [$pid, $time] = X::split(file_get_contents($path), '|');
    return (($time + $cron['cfg']['timeout']) < time());
  }

  return false;
}

