public function start(string $id_cron): bool
{
  $res = false;
  if ($this->check()
      && ($cron = $this->getCron($id_cron))
  ) {
    $enable = false;
    if ($this->db->isTriggerEnabled()) {
      $this->db->disableTrigger();
      $enable = true;
    }

    if ($this->db->update(
      $this->table, [
      'prev' => date('Y-m-d H:i:s'),
      'pid' => getmypid()
      ], [
      'id' => $id_cron,
      'pid' => null,
      'active' => 1
      ]
    )
    ) {
      $res = true;
    }

    if ($enable) {
      $this->db->enableTrigger();
    }
  }

  return $res;
}

