public function getNext($id_cron = null): ?array
{
  $conditions = [[
    'field' => 'next',
    'operator' => '<',
    'exp' => 'NOW()'
  ], [
    'field' => 'next',
    'operator' => 'isnotnull'
  ], [
    'field' => 'active',
    'value' => 1
  ]];
  if (bbn\Str::isUid($id_cron)) {
    $conditions[] = [
      'field' => 'id',
      'value' => $id_cron
    ];
  }

  if ($this->check()
      && ($data = $this->db->rselect(
        [
        'table' => $this->table,
        'fields' => [],
        'where' => [
        'conditions' => $conditions
        ],
        'order' => [[
        'field' => 'priority',
        'dir' => 'ASC'
        ], [
        'field' => 'next',
        'dir' => 'ASC'
        ]]
        ]
      ))
  ) {
    // Dans cfg: timeout, et soit: latency, minute, hour, day of month, day of week, Date
    $data['cfg'] = json_decode($data['cfg'], 1);
    return $data;
  }

  return null;
}

