        'fields' => [],
        'where' => [
        'conditions' => [[
          'field' => 'active',
          'value' => 1
        ], [
          'field' => 'pid',
          'operator' => 'isnull'
        ], [
          'field' => 'next',
          'operator' => 'isnotnull'
        ], [
          'field' => 'next',
          'operator' => '<',
