    }

    if ($this->check()
        && ($data = $this->db->rselect(
          [
          'table' => $this->table,
          'fields' => [],
          'where' => [
          'conditions' => $conditions
          ],
          'order' => [[
          'field' => 'priority',
          'dir' => 'ASC'
          ], [
          'field' => 'next',
          'dir' => 'ASC'
          ]]
          ]
        ))
    ) {
      // Dans cfg: timeout, et soit: latency, minute, hour, day of month, day of week, Date
      $data['cfg'] = json_decode($data['cfg'], 1);
      return $data;
    }

    return null;
