modelize.code:
function($table = NULL, $force = false)
  {
    $r      = [];
    $tables = false;
    if (empty($table) || ($table === '*')) {
      $tables = $this->getTables($this->current);
    }
    elseif (\is_string($table)) {
      $tables = [$table];
    }
    elseif (\is_array($table)) {
      $tables = $table;
    }

    if (\is_array($tables)) {
      foreach ($tables as $t) {
        if ($full = $this->tfn($t)) {
          $r[$full] = $this->_get_cache($full, 'columns', $force);
        }
      }

      if (\count($r) === 1) {
        return end($r);
      }

      return $r;
    }

    return null;
  }

