private function makeHash(): string
{
  $args = $this->class_cfg;
  if ((\count($args) === 1) && \is_array($args[0])) {
    $args = $args[0];
  }

  $st = '';
  foreach ($args as $a){
    $st .= \is_array($a) ? serialize($a) : '--'.$a.'--';
  }

  return md5($st);
}

