alter.code:
public function alter(string $table, array $cfg): int
{
  if (method_exists($this->language, 'alter')) {
    return $this->language->alter($table, $cfg);
  }

  if ($st = $this->language->getAlterTable($table, $cfg)) {
    return (int)$this->language->rawQuery($st);
  }

  return 0;
}

