public function getRenameDatabase(string $oldName, string $newName): ?array
{
  if ($sql = $this->getDuplicateDatabase($oldName, $newName)) {
    if (is_string($sql)) {
      $sql = [$sql];
    }

    if ($ddSql = $this->getDropDatabase($oldName)) {
      if (is_string($ddSql)) {
        $ddSql = [$ddSql];
      }

      array_push($sql, ...$ddSql);
    }

    return $sql;
  }

  return null;
}

