public function getDropKey(string $table, string $key): string
{
  if (($table = $this->tableFullName($table, true))
    && Str::checkName($key)
  ) {
    return 'ALTER TABLE '.$this->escape($table).' DROP KEY '.$this->escape($key).';';
  }

  return '';
}

