public function getTables(string $database = ''): ?array
{
  if (!$this->check()) {
    return null;
  }

  if (empty($database) || !Str::checkName($database)) {
    $database = $this->getCurrent();
  }

  $t2 = [];
  if (($r = $this->rawQuery("SHOW TABLES FROM `$database`"))
    && ($t1 = $this->fetchAllResults($r, PDO::FETCH_NUM))
  ) {
    foreach ($t1 as $t) {
      $t2[] = $t[0];
    }
  }

  return $t2;
}

