    return <<<SQL
      SELECT `TABLE_COLLATION` as collation
      FROM `information_schema`.`TABLES`
      WHERE `TABLE_SCHEMA` = "$db"
        AND `TABLE_NAME` = "$table";
    SQL;
  }

  return '';
}

