public function escape(string $item): string
{
  $items = explode('.', str_replace($this->qte, '', $item));
  $r     = [];

  foreach ($items as $m) {
    if (!Str::checkName($m)) {
      throw new Exception(X::_("Illegal name %s for the column", $m));
    }

    $r[] = $this->qte . $m . $this->qte;
  }

  return implode('.', $r);
}

