public function tableSimpleName(string $table, bool $escaped = false): ?string
{
  if ($table = trim($table)) {
    $bits = explode('.', $table);
    switch (\count($bits)) {
      case 1:
        $table = trim($bits[0], ' ' . $this->qte);
        break;
      case 2:
      case 3:
        $table = trim($bits[1], ' ' . $this->qte);
        break;
    }

    if (Str::checkName($table)) {
      return $escaped ? $this->escape($table) : $table;
    }
  }

  return null;
}

