            }
            elseif (\in_array($model['type'], static::$date_types, true)) {
              $is_date = true;
            }
          }
          elseif ($f['value'] && Str::isUid($f['value'])) {
            $is_uid = true;
          }
          elseif (\is_int($f['value']) || \is_float($f['value'])) {
            $is_number = true;
          }
        }
        else {
          $res .= (empty($res) ? '' : PHP_EOL . str_repeat(' ', $indent) . $logic . ' ') . $field . ' ';
        }

