            if ($is_uid || $is_bool || $is_number|| $is_date) {
              $res .= isset($f['exp']) ? '= ' . $f['exp'] : '= ?';
            }
            else {
              $res .= isset($f['exp']) ? 'LIKE ' . $f['exp'] : 'LIKE ?';
            }
            break;
          case '!=':
            if (isset($f['exp'])) {
              $res .= '!= ' . $f['exp'];
            }
            else {
              $res .= '!= ?';
            }
            break;
          case 'like':
            if (isset($f['exp'])) {
              $res .= 'LIKE ' . $f['exp'];
            }
            else {
              $res .= 'LIKE ?';
            }
            break;
          case 'not like':
            if (isset($f['exp'])) {
              $res .= 'NOT LIKE ' . $f['exp'];
            }
            else {
              $res .= 'NOT LIKE ?';
            }
            break;
          case 'eq':
          case 'is':
            if ($is_uid && $is_bool) {
              $res .= isset($f['exp']) ? 'LIKE ' . $f['exp'] : 'LIKE ?';
            }
            elseif ($is_uid) {
              $res .= isset($f['exp']) ? '= ' . $f['exp'] : '= ?';
            }
            else {
              $res .= isset($f['exp']) ? '= ' . $f['exp'] : ($is_number ? '= ?' : 'LIKE ?');
            }
            break;
          case 'neq':
          case 'isnot':
            if ($is_uid) {
              $res .= isset($f['exp']) ? '!= ' . $f['exp'] : '!= ?';
            }
            else {
              $res .= isset($f['exp']) ? '!= ' . $f['exp'] : ($is_number ? '!= ?' : 'NOT LIKE ?');
            }
            break;

          case 'doesnotcontains':
          case 'doesnotcontain':
            $res .= ($is_number ? '!= ' : 'NOT LIKE ') . ($f['exp'] ?? '?');
            break;

          case 'endswith':
          case 'startswith':
          case 'contains':
            $res .= 'LIKE ' . ($f['exp'] ?? '?');
            break;

          case 'gte':
          case '>=':
            $res .= '>= ' . ($f['exp'] ?? '?');
            break;

          case 'gten':
            $res .= '>= DATE_ADD(NOW(), INTERVAL ' . ($f['exp'] ?? '?');
            break;

          case 'gt':
          case '>':
            $res .= '> ' . ($f['exp'] ?? '?');
            break;

          case 'gtn':
            $res .= '> DATE_ADD(NOW(), INTERVAL '.($f['exp'] ?? '?').')';
            break;

          case 'lte':
          case '<=':
            $res .= '<= ' . ($f['exp'] ?? '?');
            break;

          case 'lten':
            $res .= '<= DATE_SUB(NOW(), INTERVAL '.($f['exp'] ?? '?').')';
            break;

          case 'lt':
          case '<':
            $res .= '< ' . ($f['exp'] ?? '?');
            break;

          case 'ltn':
            $res .= '< DATE_SUB(NOW(), INTERVAL '.($f['exp'] ?? '?').')';
            break;

          /** @todo Check if it is working with an array */
          case 'isnull':
            $res .= 'IS NULL';
            break;

          case 'isnotnull':
            $res .= 'IS NOT NULL';
            break;

          case 'isempty':
            $res .= $is_number ? '= 0' : "LIKE ''";
            break;

          case 'isnotempty':
            $res .= $is_number ? '!= 0' : "NOT LIKE ''";
            break;

          default:
            $res .= $is_uid || $is_bool || $is_number|| $is_date ? '= ?' : 'LIKE ?';
            break;
        }
      }
    }
  }

  if (!empty($res)) {
    return str_replace(PHP_EOL . PHP_EOL, PHP_EOL, $res . PHP_EOL);
  }

  return $res;
}

