            }
            elseif ($is_uid) {
              $res .= isset($f['exp']) ? '= ' . $f['exp'] : '= ?';
            }
            else {
              $res .= isset($f['exp']) ? '= ' . $f['exp'] : ($is_number ? '= ?' : 'LIKE ?');
            }
            break;
          case 'neq':
