            $res .= ($is_number ? '!= ' : 'NOT LIKE ') . ($f['exp'] ?? '?');
            break;

          case 'endswith':
          case 'startswith':
          case 'contains':
            $res .= 'LIKE ' . ($f['exp'] ?? '?');
            break;

          case 'gte':
