          $f = $tmp;
        }

        if (isset($f['field'])) {
          if (!isset($f['operator'])) {
            $f['operator'] = 'eq';
          }

          $res['conditions'][] = $f;
        }
      }
    }

    if ($full) {
      $tmp = $this->_remove_conditions_value($res);
      $res = [
        'hashed' => $tmp['hashed'],
        'values' => $tmp['values'],
        'where' => $res
      ];
    }

    return $res;
  }

  return false;
}

