_trigger.code:
protected function _trigger(array $cfg): array
{
  if ($this->_triggers_disabled) {
    if ($cfg['moment'] === 'after') {
      return $cfg;
    }

    $cfg['run']  = 1;
    $cfg['trig'] = 1;
    return $cfg;
  }

  if (!isset($cfg['trig'])) {
    $cfg['trig'] = 1;
  }

  if (!isset($cfg['run'])) {
    $cfg['run'] = 1;
  }

  if (!empty($cfg['tables']) && !empty($this->_triggers[$cfg['kind']][$cfg['moment']])) {
    $table = $this->tableFullName(is_array($cfg['tables']) ? current($cfg['tables']) : $cfg['tables']);
    // Specific to a table
    if (isset($this->_triggers[$cfg['kind']][$cfg['moment']][$table])) {
      foreach ($this->_triggers[$cfg['kind']][$cfg['moment']][$table] as $f){
        if ($f && \is_callable($f)) {
          if (!($tmp = $f($cfg))) {
            $cfg['run']  = false;
            $cfg['trig'] = false;
          }
          else{
            $cfg = $tmp;
          }
        }
      }
    }
  }

  return $cfg;
}

