_set_limit_1.code:
protected function _set_limit_1(array $args): array
{
  if (is_array($args[0])
    && (isset($args[0]['tables']) || isset($args[0]['table']))
  ) {
    $args[0]['limit'] = 1;
  }
  else {
    $start = $args[4] ?? 0;
    $num   = count($args);
    // Adding fields
    if ($num === 1) {
      $args[] = [];
      $num++;
    }

    // Adding where
    if ($num === 2) {
      $args[] = [];
      $num++;
    }

    // Adding order
    if ($num === 3) {
      $args[] = [];
      $num++;
    }

    if ($num === 4) {
      $args[] = 1;
      $num++;
    }

    $args   = array_slice($args, 0, 5);
    $args[] = $start;
  }

  return $args;
}

