makeHash.code:
protected function makeHash(): string
{
  $args = func_get_args();
  if ((\count($args) === 1) && is_array($args[0])) {
    $args = $args[0];
  }

  array_unshift($args, $this->getCurrent());
  $st = '';
  foreach ($args as $a){
    $st .= is_array($a) ? serialize($a) : '--'.$a.'--';
  }

  return $this->hash_contour.md5($st).$this->hash_contour;
}

