protected function _set_start(array $args, int $start): array
{
  if (is_array($args[0])
    && (isset($args[0]['tables']) || isset($args[0]['table']))
  ) {
    $args[0]['start'] = $start;
  }
  else {
    if (isset($args[5])) {
      $args[5] = $start;
    }
    else{
      while (count($args) < 6){
        switch (count($args)){
          case 1:
          case 2:
          case 3:
            $args[] = [];
            break;
          case 4:
            $args[] = 1;
            break;
          case 5:
            $args[] = $start;
            break;
        }
      }
    }
  }

  return $args;
}

