fmodelize.code:
public function fmodelize(string $table = '', bool $force = false): ?array
{
  if ($res = $this->modelize(...func_get_args())) {
    foreach ($res['fields'] as $n => $f){
      $res['fields'][$n]['name'] = $n;
      $res['fields'][$n]['keys'] = [];
      if (isset($res['cols'][$n])) {
        foreach ($res['cols'][$n] as $key){
          $res['fields'][$n]['keys'][$key] = $res['keys'][$key];
        }
      }
    }

    return $res['fields'];
  }

  return null;
}

