emulatePreparesAndQuery.code:
protected function emulatePreparesAndQuery(string|array $sql)
{
  $att = true;
  $emode = null;
  $res = false;
  try {
    $att = $this->pdo->getAttribute(PDO::ATTR_EMULATE_PREPARES);
  }
  catch (Exception $e) {}

  try {
    $emode = $this->pdo->getAttribute(PDO::ATTR_ERRMODE);
  }
  catch (Exception $e) {}

  if (empty($att)) {
    $this->pdo->setAttribute(PDO::ATTR_EMULATE_PREPARES, true);
  }

  $this->pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);
  if (is_string($sql)) {
    $res = $this->rawQuery($sql);
  }
  else {
    $res = true;
    foreach ($sql as $s) {
      if (!$this->rawQuery($s)) {
        $res = false;
        break;
      }
    }
  }

  if (empty($att)) {
    $this->pdo->setAttribute(PDO::ATTR_EMULATE_PREPARES, false);
  }

  if (!is_null($emode)) {
    $this->pdo->setAttribute(PDO::ATTR_ERRMODE, $emode);
  }

  return $res;
}

