dropDatabase.code:
function(string $database)
  {
    if ($this->check()) {
      if (!Str::checkName($database)) {
        throw new \Exception(X::_("Wrong database name") . " $database");
      }

      try {
        $this->rawQuery("DROP DATABASE `$database`");
      } catch (\Exception $e) {
        return false;
      }
    }

    return $this->check();
  }

