createUser.code:
function(string $user, string $pass, string $db = NULL)
  {
    if (null === $db) {
      $db = $this->getCurrent();
    }

    if (($db = $this->escape($db))
      && bbn\Str::checkName($user, $db)
      && (strpos($pass, "'") === false)
    ) {
      return (bool)$this->rawQuery(
        <<<MYSQL
CREATE USER '$user'@'{$this->getHost()}' IDENTIFIED BY '$pass';
GRANT SELECT,INSERT,UPDATE,DELETE,CREATE,DROP,INDEX,ALTER
ON $db . *
TO '$user'@'{$this->getHost()}';
MYSQL
      );
    }

    return false;
  }

deleteUser.code:
function(string $user)
  {
    if (bbn\Str::checkName($user)) {
      $host = $this->getHost();
      $this->rawQuery("REVOKE ALL PRIVILEGES ON *.* FROM '$user'@'$host'");
      return (bool)$this->rawQuery("DROP USER '$user'@'$host'");
    }

    return false;
  }

getUsers.code:
function(string $user = '', string $host = '')
  {
    if ($this->check()) {
      $cond = '';
      if (!empty($user) && bbn\Str::checkName($user)) {
        $cond .= " AND  user LIKE '$user' ";
      }

      if (!empty($host) && bbn\Str::checkName($host)) {
        $cond .= " AND  host LIKE '$host' ";
      }

      $us = $this->getRows(
        <<<MYSQL
SELECT DISTINCT host, user
FROM mysql.user
WHERE 1
$cond
MYSQL
      );
      $q = [];
      foreach ($us as $u) {
        $gs = $this->getColArray("SHOW GRANTS FOR '$u[user]'@'$u[host]'");
        foreach ($gs as $g) {
          $q[] = $g;
        }
      }

      return $q;
    }

    return null;
  }

renameTable.code:
function(string $table, string $newName)
  {
    if ($this->check() && Str::checkName($table) && Str::checkName($newName)) {
      $t1 = strpos($table, '.') ? $this->tableFullName($table, true) : $this->tableSimpleName($table, true);
      $t2 = strpos($newName, '.') ? $this->tableFullName($newName, true) : $this->tableSimpleName($newName, true);

      $res = $this->rawQuery(sprintf("RENAME TABLE %s TO %s", $t1, $t2));
      return (bool)$res;
    }

    return false;
  }

getTableComment.code:
function(string $table)
  {
    if ($tmp = $this->tableFullName($table)) {
      $bits = X::split($tmp, '.');
      return $this->getOne(
          "SELECT table_comment
        FROM INFORMATION_SCHEMA.TABLES 
        WHERE table_schema = ?
        AND table_name = ?",
          $bits[0],
          $bits[1]
        ) ?? '';
    }

    return '';
  }

