function()
  {
    if (!$this->check()) {
      return null;
    }

    $x = [];
    if ($r = $this->rawQuery('SHOW DATABASES')) {
      $x = array_map(
        function ($a) {
          return $a['Database'];
        }, array_filter(
          $this->fetchAllResults($r, \PDO::FETCH_ASSOC), function ($a) {
          return ($a['Database'] === 'information_schema') || ($a['Database'] === 'mysql') ? false : 1;
        }
        )
      );
      sort($x);
    }

    return $x;
  }

