tableFullName.code:
function(string $table, bool $escaped = false)
  {
    $bits = explode('.', $table);

    if (\count($bits) === 3) {
      $db    = trim($bits[0], ' ' . $this->qte);
      $table = trim($bits[1]);
    } elseif (\count($bits) === 2) {
      $db    = trim($bits[0], ' ' . $this->qte);
      $table = trim($bits[1], ' ' . $this->qte);
    } else {
      $db    = $this->getCurrent();
      $table = trim($bits[0], ' ' . $this->qte);
    }

    if (Str::checkName($db) && Str::checkName($table)) {
      return $escaped
        ? $this->escape("$db.$table")
        : "$db.$table";
    }

    return null;
  }

