modelize.code:
function($table = NULL, bool $force = false)
  {
    $r      = [];
    $tables = false;
    if (empty($table) || ($table === '*')) {
      $tables = $this->getTables();
    }
    elseif (\is_string($table)) {
      $tables = [$table];
    }
    elseif (\is_array($table)) {
      $tables = $table;
    }

    if (\is_array($tables)) {
      foreach ($tables as $t) {
        if ($full = $this->tableFullName($t)) {
          $r[$full] = $this->_get_cache($full, 'columns', $force);
        }
      }

      if (\count($r) === 1) {
        return end($r);
      }

      return $r;
    }

    return null;
  }

