reprocessCfg.code:
function(array $cfg)
  {
    unset($cfg['bbn_db_processed']);
    unset($cfg['bbn_db_treated']);

    if (isset($cfg['hash'])) {
      unset($this->cfgs[$cfg['hash']]);
    }

    $tmp = $this->processCfg($cfg, true);

    if (!empty($cfg['values']) && (count($cfg['values']) === count($tmp['values']))) {
      $tmp = array_merge($tmp, ['values' => $cfg['values']]);
    }

    return $tmp;
  }

