function($id = '')
  {
    if ($id === '') {
      if ($this->id_just_inserted) {
        $id                     = $this->id_just_inserted;
        $this->id_just_inserted = null;
      }
      else{
        $id = $this->pdo->lastInsertId();
        if (\is_string($id) && Str::isInteger($id) && ((int)$id != PHP_INT_MAX)) {
          $id = (int)$id;
        }
      }
    }
    else{
      $this->id_just_inserted = $id;
    }

    $this->last_insert_id = $id;
    return $this;
  }

