insert.code:
function($table, array $values = NULL, bool $ignore = false)
  {
    if (empty($table)) {
      throw new \Exception(X::_('Table name is not specified'));
    }

    if (\is_array($table) && isset($table['values'])) {
      $values = $table['values'];
    }

    // Array of arrays
    if (\is_array($values)
      && count($values)
      && !X::isAssoc($values)
      && \is_array($values[0])
    ) {
      $res = 0;

      foreach ($values as $v){
        $res += $this->insert(
          is_array($table)
            ? ($table['tables'][0] ?? '')
            : $table,
          $v,
          is_array($table)
            ? ($table['ignore'] ?? $ignore)
            : $ignore
        );
      }

      return $res;
    }

    $cfg         = \is_array($table) ? $table : [
      'tables' => [$table],
      'fields' => $values,
      'ignore' => $ignore
    ];
    $cfg['kind'] = 'INSERT';
    return $this->_exec($cfg);
  }

