public function init(array $values = []): self
{
  $this->values = $values;
  $this->res    = null;
  return $this;
}

execute.code:
public function execute(?array $args = null): bool
{
  if (($this->res === null) || ($args !== null)) {
    $this->res = 1;
    if (\is_array($args)) {
      try{
        return parent::execute($args);
      }
      catch (PDOException $e){
        $this->db->error($e);
      }
    }
    elseif ($args !== null) {
      $args = \func_get_args();
      try{
        return parent::execute(...$args);
      }
      catch (PDOException $e){
        $this->db->error($e);
      }
    }
    else{
      if ($this->values && \is_array($this->values) && count($this->values)) {
        foreach ($this->values as $i => $v){
          if (Str::isBuid($v)) {
            if ($this->db->getEngine() === 'pgsql') {
              $this->bindValue($i + 1, $v, PDO::PARAM_LOB);
            } else {
              $this->bindValue($i + 1, $v);
            }
          }
          else{
            if (\is_int($v)) {
              $param = PDO::PARAM_INT;
            }
            elseif (\is_bool($v)) {
              $param = PDO::PARAM_BOOL;
            }
            elseif ($v === null) {
              $param = PDO::PARAM_NULL;
            }
            else {
              $param = PDO::PARAM_STR;
            }

            $this->bindValue($i + 1, $v, $param);
          }
        }
      }

      try{
        return parent::execute();
      }
      catch (PDOException $e){
        $this->db->error($e);
      }
    }
  }

  return false;
}

