public function fetch(int $mode = PDO::FETCH_BOTH, int $orientation = PDO::FETCH_ORI_NEXT, int $offset = 0): mixed
{
  $this->execute();
  return $this->db->correctTypes(parent::fetch($mode, $orientation, $offset));
}

_fetchAll.code:
public function _fetchAll($fetch_style = PDO::FETCH_BOTH, $fetch_argument = false, $ctor_args = false)
{
  $this->execute();
  if ($ctor_args) {
    $res = parent::fetchAll($fetch_style, $fetch_argument, $ctor_args);
  }
  elseif ($fetch_argument) {
    $res = parent::fetchAll($fetch_style, $fetch_argument);
  }
  else{
    $res = parent::fetchAll($fetch_style);
  }

  return $this->db->correctTypes($res);
}

